import React from 'react'

const BooksSection = ({ data }) => {
    
    return (
        <div className="d-flex justify-content-around align-items-center flex-wrap ">
            {data && data.map((item,index) => (
            <div 
                className="m-5" 
                style={{
                    width:"200px",
                    height:"420px",
                    border:"1px solid #8a675c",
                    borderRadius:"20px",
                    background: "linear-gradient(to right, #ff7e5f, #feb47b)"
                }}
            > 
                <div>
                    <img 
                        style={{
                            width:"200px",
                            height:"250px",
                            borderTopLeftRadius:"20px",
                            borderTopRightRadius:"20px"
                        }} 
                        className="img-fluid" 
                        src={item.image} 
                        alt="/" 
                    />
                </div>
                <h5 style={{fontSize:"19px",color:"#f5dab0",fontFamily:"Garamond",fontWeight:"bold"}} className="p-2 my-1 ">
                    {item.bookname.slice(0,30)}..
                </h5>
                <b 
                    style={{fontSize:"19px",color:"silver",fontFamily:"Garamond"}}
                    className="m-0 px-2">Rs. {item.price}
                </b> 
                <div className="d-flex justify-content-around align-items-center my-2 p-2 m-2"
                 style={{fontSize:"20px",color:"beige",fontFamily:"Garamond"}}>
                    <button className="btn btn-secondary"><b>Update</b></button>
                    <button className="btn btn-danger"><b>üóëÔ∏è</b></button>
                </div>
            </div>
            ))}
        </div>
    )
}

export default BooksSection
