import React from 'react'
import { useState } from 'react'

const BooksSection = ({ data }) => {
const [booksData, setBooksData] = useState(data);

  const handleDelete = (index) => {
    // Create a copy of the current state and remove the item at the specified index
    const updatedData = [...booksData];
    updatedData.splice(index, 1);
    // Update the state with the new array
    setBooksData(updatedData);
  };

  const handleUpdate = (index) => {
    // Implement your update logic here, e.g., show a modal or navigate to an update page
    console.log(`Update book at index ${index}`);
  };


const BooksSection = ({ data }) => {
    
    return (
        <div className="d-flex justify-content-around align-items-center flex-wrap ">
            {data && data.map((item,index) => (
            <div 
                className="m-3" 
                style={{
                    width:"230px",
                    height:"420px",
                    border:"2px solid black",
                    borderRadius:"20px",
                    background: "linear-gradient(to right,#331913,#190c09"
                    
    
                }}
            > 
   
                <div>
                    <img 
                        style={{
                            width:"230px",
                            height:"250px",
                            borderTopLeftRadius:"20px",
                            borderTopRightRadius:"20px"
                        }} 
                        className="img-fluid" 
                        src={item.image} 
                        alt="/" 
                    />
                </div>
                <h5 style={{fontSize:"20px",color:"#f5dab0",fontFamily:"Garamond",fontWeight:"bold"}} className="p-2 my-1 ">
                    {item.bookname.slice(0,20)}..
                </h5>
                <b 
                    style={{fontSize:"19px",color:"silver",fontFamily:"Garamond"}}
                    className="m-0 px-2">Rs. {item.price}
                </b> 
                <div className="d-flex justify-content-around align-items-center my-2 p-2 m-2"
                 style={{fontSize:"20px",color:"beige",fontFamily:"Garamond"}}>
                    <button className="btn btn-secondary"  onClick={() => handleUpdate(index)}><b>Update</b></button>
                    <button className="btn btn-danger" onClick={() => handleDelete(index)} ><b>üóëÔ∏è</b></button>
                </div>
            </div>
            ))}
        </div>
    )
}

}

export default BooksSection
